package day37.Servlet_To_DataBase;

public class SetAttr {
	
		private String name;
		private String mob;
		private String mail; 
		private String pass;
		public SetAttr() {
			super();
			// TODO Auto-generated constructor stub
		}
		public SetAttr(String name, String mob, String mail, String pass) {
			super();
			this.name = name;
			this.mob = mob;
			this.mail = mail;
			this.pass = pass;
		}
		public String getName() {
			return name;
		}
		public void setName(String name) {
			this.name = name;
		}
		public String getMob() {
			return mob;
		}
		public void setMob(String mob) {
			this.mob = mob;
		}
		public String getMail() {
			return mail;
		}
		public void setMail(String mail) {
			this.mail = mail;
		}
		public String getPass() {
			return pass;
		}
		public void setPass(String pass) {
			this.pass = pass;
		}
		
	
		
		
		
		
}
